import {useState} from 'react';
import styles from './UiInput.module.scss';
import cn from 'classnames';
import {ReactComponent as EyeOn} from "../../../images/icons/eye-on.svg";
import {ReactComponent as EyeOff} from "../../../images/icons/eye-off.svg";

export const UiInput = (props) => {
    const [type, setType] = useState('password');
    setType(props.type);
    return (
        <div className={cn(styles.input, props.className)}>
            <div className={styles.input__wrapper}>
                <div className={styles.input__icon}>
                    {props.icon}
                </div>
                <input
                    type={props.type}
                    spellCheck='false'
                    autoComplete='on'
                    className={styles.input__field}
                    placeholder={props.placeholder}
                    value={props.value}
                    onChange={(e)=>props.setValue(e.target.value)}
                />
            </div>
            {props.format === 'password' && (
                <button
                    onClick={
                        ()=>{
                            if(props.type==='password') props.setType('text');
                            else props.setType('password');
                        }
                    }
                    // onClick={()=>type==='password'? setType('text'):setType('password')
                    // }
                    type='button'
                >
                    {props.type==='text'?<EyeOn/>:<EyeOff/>}
                </button>
            )}
        </div>
    );
};
