const SET_USER = 'SET_ESER';
const defaultState={
    currentUser: {},
    isAuth: false
}
export default function userReducer(state=defaultState, action){
    switch(action.type){
        case SET_USER:
            return {
                ...state,
                currentUser: action.payload.user,
            }
        default:
            return state
    }
}
